import React from "react"

export default function MainContent(){
    const [meme, setMeme] = React.useState(
        {
            "top-text": "",
            "bottom-text":"",
            "image": "https://i.imgflip.com/1g8my4.jpg"
        }
    )

    const [allMemes, setAllMemes] = React.useState([])

    React.useEffect(()=>{
        fetch("https://api.imgflip.com/get_memes")
            .then (resp => resp.json())
            .then (data => setAllMemes(data.data.memes))
    }, [])

    function getMemeImage(){
        let randomNum = Math.floor(Math.random()*allMemes.length)

        const url = allMemes[randomNum].url
        setMeme( prevState => ({
            ...prevState, 
            "image": url
        }
    ))
    }

    function valueChange(event){
        setMeme(prevMeme => ({
            ...prevMeme, 
            [event.target.name]: event.target.value
        }))
    }

    return(<div className="MemeComp">
        <div className="form">
            <input className = "get" name = "top-text" type='text' placeholder="top text" onChange = {valueChange}></input>
            <input className = "get" name="bottom-text" type='text' placeholder="bottom text" onChange={valueChange}></input>
            <button onClick = {getMemeImage} className = "gett">Get a new meme image  ðŸ–¼</button>
        </div>
        <h1 className="top-text meme">{meme.top-text}</h1>
        <img className = "meme--image"src={meme.image}></img>
        <h1 className="bottom-text meme">{meme.button-text}</h1>
    </div>)
}
